{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "npm install -g npm@latest",
        "cd infrastructure",
        "npm ci",
        "npm run build",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "infrastructure/cdk.out",
    "files": "**/*"
  }
}
